sys_script_include.script:
function validateEmailAddress(email_address_string) {
	// type coercion to guarantee email is a string
	email_address_string = email_address_string + '';

	if(email_address_string.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else {
		 return false;
	}
}
